provider = HTTPProvider('https://optimism.publicnode.com')
web3 = Web3(provider)

with open('calculated_pairs.json') as f:
    calculated_pairs = json.loads(f.read())

aggregated_pairs = []
for pair in tqdm(calculated_pairs):
    if pair['value0'] < 10 and pair['value1'] < 10:  # too low liquidity
        continue

    if abs(1 - pair['price0']) < 0.1 and abs(1 - pair['price1']) < 0.1:  # filter out stablecoin swaps
        continue

    pair_contract = web3.eth.contract(address=pair['pair_address'], abi=PAIR_ABY)

    experimental_value = abs(pair['value0'] - pair['value1']) / 2
    value_out_0 = pair_contract.functions.getAmountOut(int(experimental_value / pair['price1'] * 10 ** pair['decimals1']), pair['token1']).call() / 10 ** pair['decimals0'] * pair['price0']
    value_out_1 = pair_contract.functions.getAmountOut(int(experimental_value / pair['price0'] * 10 ** pair['decimals0']), pair['token0']).call() / 10 ** pair['decimals1'] * pair['price1']
    if value_out_0 - experimental_value < 3 and value_out_1 - experimental_value < 3:
        continue

    pair['experimental_value'] = experimental_value
    pair['value_out_0'] = value_out_0
    pair['value_out_1'] = value_out_1
    aggregated_pairs.append(pair)
    with open('aggregated_pairs.json', 'w') as f:
        f.write(json.dumps(aggregated_pairs, indent=4))
