provider = HTTPProvider('https://optimism.publicnode.com')
web3 = Web3(provider)

pair_factory = web3.eth.contract(address='0x25CbdDb98b35ab1FF77413456B31EC81A6B6B746', abi=FACTORY_ABI)

for pair_index in tqdm(range(604)):
    try:
        pair_address = pair_factory.functions.allPairs(pair_index).call()
        pair_contract = web3.eth.contract(address=pair_address, abi=PAIR_ABY)
        reserve0 = pair_contract.functions.reserve0().call()
        reserve1 = pair_contract.functions.reserve1().call()
        token0 = pair_contract.functions.token0().call()
        token1 = pair_contract.functions.token1().call()
        token0_contract = web3.eth.contract(address=token0, abi=USDC_TOKEN_ABI)
        token1_contract = web3.eth.contract(address=token1, abi=USDC_TOKEN_ABI)
        decimals0 = token0_contract.functions.decimals().call()
        decimals1 = token1_contract.functions.decimals().call()
        name0 = token0_contract.functions.name().call()
        name1 = token1_contract.functions.name().call()
        symbol0 = token0_contract.functions.symbol().call()
        symbol1 = token1_contract.functions.symbol().call()
        
        with open('pairs.json', 'r') as f:
            pairs: list = json.loads(f.read())

        pairs.append({
            'pair_address': pair_address,
            'token0': token0,
            'token1': token1,
            'reserve0': reserve0,
            'reserve1': reserve1,
            'decimals0': decimals0,
            'decimals1': decimals1,
            'name0': name0,
            'name1': name1,
            'symbol0': symbol0,
            'symbol1': symbol1
        })

        with open('pairs.json', 'w') as f:
            json.dump(pairs, f, indent=4)

    except Exception as e:
        print(f'Got exception {traceback.format_exc()} at index {pair_index}')
